import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { NavService } from 'src/app/services/nav.service';

@Component({
  selector: 'app-sample-page',
  templateUrl: './sample-page.component.html'
})
export class AppSamplePageComponent implements OnInit {
  url: string | ArrayBuffer | null;
  format: string;
  @Output() uploadedVideoFile = new EventEmitter();
  showVBtn = false;
  message = '';
  constructor(private router: Router, private navService: NavService) { }

  ngOnInit(): void {
  }

  onSelectFile(event: any) {
    const file = event.target.files && event.target.files[0];
    if (file) {
      var reader = new FileReader();
      reader.readAsDataURL(file);
      if(file.type.indexOf('image')> -1){
        this.format = 'image';
      } else if(file.type.indexOf('video')> -1){
        this.format = 'video';
      }
      reader.onload = (event) => {
        // this.url = (<FileReader>event.target).result;
        console.log(this.url);
        this.navService.upload(this.url).subscribe({
          next: (event: any) => {
            console.log(event);
            // if (event.type === HttpEventType.UploadProgress) {
            //   this.progress = Math.round(100 * event.loaded / event.total);
            // } else if (event instanceof HttpResponse) {
            //   this.message = event.body.message;
            //   this.fileInfos = this.uploadService.getFiles();
            // }
          },
          error: (err: any) => {
            console.log(err);
            // this.progress = 0;

            if (err.error && err.error.message) {
              this.message = err.error.message;
            } else {
              this.message = 'Could not upload the file!';
            }

            // this.currentFile = undefined;
          }
        });

        // this.goToViewPage();
        // this.showVBtn = true;
      }
    }
  }

  goToViewPage(){
    console.log(this.url);
    // return;
    // this.uploadedVideoFile.emit(this.url);
    // this.navService.viewVd.subscribe((url: any) => {
    //   return url;
    // });
    this.navService.setViewVd = this.url;
    this.router.navigate(['extra/view-video']);
  }

}
