import { ChangeDetectionStrategy, Component, OnDestroy, OnInit } from '@angular/core';
import {MatSnackBar, MatSnackBarHorizontalPosition, MatSnackBarVerticalPosition} from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { NavService } from 'src/app/services/nav.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AppSideLoginComponent implements OnInit, OnDestroy {
  usrNm: any;
  pwd: any;
  horizontalPosition: MatSnackBarHorizontalPosition = 'end';
  verticalPosition: MatSnackBarVerticalPosition = 'top';
  loginFn: any;
  constructor(private _snackBar: MatSnackBar, private navService: NavService, private router: Router) {}

  ngOnInit(): void {
    
  }

  gotoVdPg() {
    console.log(this.usrNm);
    console.log(this.pwd);
    if (this.usrNm === undefined || this.usrNm === '' || this.usrNm === ' ') {
      this._snackBar.open("Please enter username", "Close", {
        horizontalPosition: this.horizontalPosition,
        verticalPosition: this.verticalPosition,
        duration: 1500
      });
    } else if (this.pwd === undefined || this.pwd === '' || this.pwd === ' ') {
      this._snackBar.open("Please enter password", "Close", {
        horizontalPosition: this.horizontalPosition,
        verticalPosition: this.verticalPosition,
        duration: 1500
      });
    } else {
      this.loginFn = this.navService.getLoginDtls().subscribe((res) => {
        console.log(res);
        console.log("in res");
        let lgn_dtls = JSON.parse(JSON.stringify(res));
        console.log(lgn_dtls.body);
        if(lgn_dtls){
          if(this.usrNm === lgn_dtls.body.username && this.pwd === lgn_dtls.body.password){
            console.log("login");
            this.router.navigateByUrl("/extra/sample-page");
          } else {
            this._snackBar.open("Please enter a valid username and password", "Close", {
              horizontalPosition: this.horizontalPosition,
              verticalPosition: this.verticalPosition,
              duration: 5500
            });
          }
        }
        
      })
    }
  }

  ngOnDestroy(): void {
    // if(this.loginFn){
      this.loginFn.unsubscribe();
    // }
  }
}
<div class="blank-layout-container justify-content-center align-items-center bg-light-primary">
  <div
    class="position-relative row w-100 h-100 bg-gredient justify-content-center"
  >
    <div class="col-lg-4 d-flex align-items-center">
      <mat-card class="cardWithShadow boxed-auth">
        <mat-card-content class="p-32">
          <div class="text-center">
            <img
                src="./assets/images/logos/logo.png"
                class="align-middle m-2"
                alt="logo"
              />
          </div>
          <form >
            <mat-label class="mat-subtitle-2 f-s-14 f-w-600 m-b-12 d-block"
              >Username</mat-label
            >
            <mat-form-field appearance="outline" class="w-100" color="primary">
              <input matInput [(ngModel)]="usrNm" placeholder="Enter Username" name="usrNm"/>
            </mat-form-field>
            <!-- password -->
            <mat-label class="mat-subtitle-2 f-s-14 f-w-600 m-b-12 d-block"
              >Password</mat-label
            >
            <mat-form-field appearance="outline" class="w-100" color="primary">
              <input matInput type="password" [(ngModel)]="pwd" placeholder="Enter Password" name="pwd"/>
            </mat-form-field>
            <!-- [routerLink]="['/extra/sample-page']" -->
            <button  mat-flat-button color="primary" class="w-100" (click)="gotoVdPg()">
              Sign In
            </button>
            <!-- input -->
          </form>
        </mat-card-content>
      </mat-card>
    </div>
  </div>
</div>
