import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { NavService } from 'src/app/services/nav.service';

@Component({
  selector: 'app-sample-page',
  templateUrl: './sample-page.component.html'
})
export class AppSamplePageComponent implements OnInit {
  url: string | ArrayBuffer | null;
  format: string;
  @Output() uploadedVideoFile = new EventEmitter();
  showVBtn = false;
  message = '';
  slctdFleNm: any;
  upldFle: any;
  constructor(private router: Router, private navService: NavService) { }

  ngOnInit(): void {
  }

  onSelectFile(event: any) {
    console.log(event);
    // return;
    const file = event.target.files && event.target.files[0];
    this.upldFle = file;
    this.slctdFleNm = file.name;
    console.log(file);
    console.log("----------------------------------")
    if (file) {
      var reader = new FileReader();
      reader.readAsDataURL(file);
      if(file.type.indexOf('image')> -1){
        this.format = 'image';
      } else if(file.type.indexOf('video')> -1){
        this.format = 'video';
      }
      reader.onload = (event) => {
        this.url = (<FileReader>event.target).result;
      }
    }
  }

  goToViewPage(){
    this.navService.upload(this.upldFle).subscribe({
      next: (event: any) => {},
      error: (err: any) => {
        console.log(err);
        if (err.error && err.error.message) {
          this.message = err.error.message;
        } else {
          this.message = 'Could not upload the video!';
        }
      }
    });
    this.navService.setViewVd = this.url;
    this.router.navigate(['extra/view-video']);
  }

}
