import { ChangeDetectionStrategy, Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { NavService } from 'src/app/services/nav.service';
import { MatSnackBar, MatSnackBarHorizontalPosition, MatSnackBarVerticalPosition } from '@angular/material/snack-bar';

@Component({
  selector: 'app-upload-video',
  templateUrl: './upload-video.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AppUploadVideoComponent implements OnInit {
  url: string | ArrayBuffer | null;
  format: string;
  @Output() uploadedVideoFile = new EventEmitter();
  showVBtn = false;
  message = '';
  slctdFleNm: any;
  upldFle: any;
  horizontalPosition: MatSnackBarHorizontalPosition = 'end';
  verticalPosition: MatSnackBarVerticalPosition = 'top';
  delId: any;
  constructor(private router: Router, private navService: NavService, private _snackBar: MatSnackBar) { }

  ngOnInit(): void {
  }

  createJsonObj(){
    let stcObj = [
      {
        "id": 1,
        "title": "First Object",
        "description": "This is the description of the first object."
      },
      {
        "id": 2,
        "title": "Second Object",
        "description": "This is the description of the second object."
      },
      {
        "id": 3,
        "title": "Third Object",
        "description": "This is the description of the third object."
      }
    ]
    this.navService.postJsonData(stcObj).subscribe(res => {
      console.log(res);
    })
  }

  fetchJsonObj(){
    this.navService.getJsonData().subscribe((res) => {
      console.log(res);
    })
  }

  updateJsonObj(){
    let stcObj = [
      {
        "id": 1,
        "title": "First Object",
        "description": "This is the description of the first object."
      },
      {
        "id": 2,
        "title": "Second Object",
        "description": "This is the description of the second object."
      },
      {
        "id": 3,
        "title": "Third Object",
        "description": "This is the description of the third object."
      }
    ]
    this.navService.postUpdateJsonData(stcObj).subscribe(res => {
      console.log(res);
    })
  }

  deleteJsonObj(){
    console.log(this.delId);
  }
}
